import React, { useState, useEffect } from 'react';
import StudentService from '../../services/student.service';
import ProgramService from '../../services/program.service';
import BatchService from '../../services/batch.service';
import DepartmentService from '../../services/department.service';
import DegreeLevelService from '../../services/degreeLevel.service';
import LocationService from '../../services/location.service';
import AdmissionService from '../../services/admission.service';
import MaritalStatusService from '../../services/maritalStatus.service';
import StudentStatusService from '../../services/studentStatus.service';
import UserService from '../../services/user.service';
import ParentService from '../../services/parent.service';
import statsService from '../../services/stats.service';
import { toast } from 'react-toastify';
import StatCard from '../../components/StatCard';
import StudentForm from '../../components/StudentForm';
import {
  UserGroupIcon,
  PlusIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  EyeIcon,
  PencilIcon,
  TrashIcon,
  AcademicCapIcon,
  PhoneIcon,
  EnvelopeIcon,
  MapPinIcon,
  CalendarIcon,
  UserIcon,
  DocumentTextIcon,
  ChartBarIcon
} from '@heroicons/react/24/outline';

const Students = () => {
  const [students, setStudents] = useState([]);
  const [programs, setPrograms] = useState([]);
  const [filteredPrograms, setFilteredPrograms] = useState([]);
  const [batches, setBatches] = useState([]);
  const [provinces, setProvinces] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [communes, setCommunes] = useState([]);
  const [villages, setVillages] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  const [maritalStatuses, setMaritalStatuses] = useState([]);
  const [studentStatuses, setStudentStatuses] = useState([]);
  const [parents, setParents] = useState([]);
  const [users, setUsers] = useState([]);
  const [degreeLevels, setDegreeLevels] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingStudent, setEditingStudent] = useState(null);
  const [viewStudent, setViewStudent] = useState(null);
  const [confirmDelete, setConfirmDelete] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProgram, setSelectedProgram] = useState('');
  const [selectedBatch, setSelectedBatch] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    graduated: 0,
    suspended: 0
  });

  useEffect(() => {
    fetchStudents();
    fetchPrograms();
    fetchBatches();
    fetchProvinces();
    fetchDistricts();
    fetchCommunes();
    fetchVillages();
    fetchAdmissions();
    fetchMaritalStatuses();
    fetchStudentStatuses();
    fetchParents();
    fetchUsers();
    fetchDegreeLevels();
    fetchDepartments();
    fetchStats();
  }, [currentPage, searchTerm, selectedProgram, selectedBatch]);

  const fetchStudents = async () => {
    setLoading(true);
    try {
      const response = await StudentService.getAll(
        currentPage,
        10,
        searchTerm,
        selectedProgram,
        selectedBatch
      );

      console.log('Students API response:', response);

      // StudentService now handles errors internally and returns a consistent structure
      if (response.success === false) {
        setError(response.error || 'Failed to fetch students');
        console.error('Error in fetchStudents:', response.error);
        setStudents([]);

        // Handle authentication errors specifically
        if (response.isAuthError) {
          // Store that we had an auth error
          localStorage.setItem('auth_failed', 'true');

          // Don't automatically redirect - let the user choose
          console.log('Authentication error detected in students page');
        }
      } else {
        // Handle different response formats
        if (response.data && Array.isArray(response.data)) {
          // Standard format from our service
          setStudents(response.data);
          setTotalPages(response.totalPages || Math.ceil((response.count || 0) / 10));
          setError('');
        } else if (response.rows && Array.isArray(response.rows)) {
          // Node.js format with rows property
          setStudents(response.rows);
          setTotalPages(Math.ceil((response.count || 0) / 10));
          setError('');
        } else if (Array.isArray(response)) {
          // Simple array format
          setStudents(response);
          setTotalPages(Math.ceil(response.length / 10));
          setError('');
        } else {
          // Unexpected format
          console.error('Unexpected students response format:', response);
          setStudents([]);
          setTotalPages(1);
          setError('Received unexpected data format from server');
        }
      }
    } catch (err) {
      // This catch is now a fallback for unexpected errors
      setError('Failed to fetch students. Please try again later.');
      console.error('Unexpected error in fetchStudents:', err);
      setStudents([]);
      setTotalPages(1);
    } finally {
      setLoading(false);
    }
  };

  const fetchPrograms = async () => {
    try {
      const response = await ProgramService.getAll();
      console.log('Programs API response:', response);

      // Handle different response formats
      if (response && response.data && Array.isArray(response.data)) {
        // Standard format from our service
        setPrograms(response.data);
      } else if (response && response.rows && Array.isArray(response.rows)) {
        // Node.js format with rows property
        setPrograms(response.rows);
      } else if (Array.isArray(response)) {
        // Simple array format
        setPrograms(response);
      } else {
        console.error('Unexpected programs response format:', response);
        // Fallback data
        setPrograms([
          // Associate Degree Programs
          { id: 1, name: 'Software Development', degree_level_id: 2, department_id: null },
          { id: 2, name: 'System and Network Administration', degree_level_id: 2, department_id: null },
          { id: 3, name: 'Food Processing', degree_level_id: 2, department_id: null },
          { id: 4, name: 'Integrated Farming System', degree_level_id: 2, department_id: null },
          { id: 5, name: 'Veterinary Medicine', degree_level_id: 2, department_id: null },
          // Bachelor Degree Programs
          { id: 6, name: 'Computer Science', degree_level_id: 1, department_id: 1 },
          { id: 7, name: 'Information Systems', degree_level_id: 1, department_id: 1 },
          { id: 8, name: 'English Literature', degree_level_id: 1, department_id: 2 },
          { id: 9, name: 'Tourism and Hospitality', degree_level_id: 1, department_id: 3 },
        ]);
      }
    } catch (err) {
      console.error('Error fetching programs:', err);
      // Fallback data
      setPrograms([
        // Associate Degree Programs
        { id: 1, name: 'Software Development', degree_level_id: 2, department_id: null },
        { id: 2, name: 'System and Network Administration', degree_level_id: 2, department_id: null },
        { id: 3, name: 'Food Processing', degree_level_id: 2, department_id: null },
        { id: 4, name: 'Integrated Farming System', degree_level_id: 2, department_id: null },
        { id: 5, name: 'Veterinary Medicine', degree_level_id: 2, department_id: null },
        // Bachelor Degree Programs
        { id: 6, name: 'Computer Science', degree_level_id: 1, department_id: 1 },
        { id: 7, name: 'Information Systems', degree_level_id: 1, department_id: 1 },
        { id: 8, name: 'English Literature', degree_level_id: 1, department_id: 2 },
        { id: 9, name: 'Tourism and Hospitality', degree_level_id: 1, department_id: 3 },
      ]);
    }
  };

  const fetchBatches = async () => {
